case class Person( firstName: String, lastName: String, birthDate: java.util.Date, age: Option[Long] = None, fullName: Option[String] = None)
def addFullName(person: Person) = person.copy(fullName = Some(s"${person.firstName} ${person.lastName}")) def addAge(person: Person) = person.copy(age = Some((now - person.birthDate.getTime) / MilliSecondsInYear))

class PimpAny[A](any: A) {
  def |>[B](fun: A ⇒ B): B = fun(any)
}

implicit class PimpAnyWithPipeOperator[A](any: A) {
  def |>[B](fun: A ⇒ B): B = fun(any)
}

implicit class PimpAnyWithPipeOperator[A](any: A) { def |>[B](fun: A ⇒ B): B = fun(any) }
val MilliSecondsInYear = 1000 * 60 * 60 * 24 * 365L
case class Person( firstName: String, lastName: String, birthDate: java.util.Date, age: Option[Long] = None, fullName: Option[String] = None)
def now = System.currentTimeMillis
def addFullName(person: Person): Person = person.copy(fullName = Some(s"${person.firstName} ${person.lastName}"))
def addAge(person: Person): Person = person.copy(age = Some((now - person.birthDate.getTime) / MilliSecondsInYear))
val hans = Person("Herbert", "Fischer", new java.util.Date(now - (23 * MilliSecondsInYear))) val withAgeAndFullName = hans |> addAge |> addFullName
